package swp_compiler_ss13.common.backend;

import java.io.InputStream;
import java.util.List;
import java.util.Map;

public interface Backend {
	/**
	 * Generate target code for the provided TAC.
	 * 
	 * @param tac
	 *            generated by frontend
	 * @return map whose keys are the output file names
	 * @throws BackendException
	 *             for errors in backend processing
	 */
	Map<String, InputStream> generateTargetCode(List<Quadruple> tac) throws BackendException;
}
