package swp_compiler_ss13.common.backend;

import java.io.InputStream;
import java.util.List;
import java.util.Map;

public interface Backend {
	/**
	 * Generate target code for the provided TAC. For naming of output files the
	 * baseFileName has to be provided. The backend decide the exact filename of the
	 * output files inclusive the file extension by the keys of the return map.
	 * 
	 * @param baseFileName
	 *            of the input source file without file extension
	 * @param tac
	 *            generated by frontend
	 * @return map whose keys are the output file names
	 * @throws BackendException
	 *             for errors in backend processing
	 */
	Map<String, InputStream> generateTargetCode(String baseFileName, List<Quadruple> tac) throws BackendException;
}
